INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910204', 'Come, $n. Lord Stormrage awaits.', 'OVERLORD_SAY_1');
INSERT INTO `script_texts` (`entry`, `content_default`, `type`, `comment`) VALUES ('-1910205', "Warriors of Dragonmaw, gather 'round! One among you has attained the rank of highlord! Bow your heads in reverence! Show your respect and allegiance to Highlord $n!", '1', 'OVERLORD_YELL_1');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910206', 'Lord Illidan will be here shortly.', 'OVERLORD_SAY_2');
INSERT INTO `script_texts` (`entry`, `content_default`, `type`, `comment`) VALUES ('-1910207', 'All hail Lord Illidan!', '1', 'OVERLORD_YELL_2');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910208', 'Lord Illidan, this is the Dragonmaw that I, and others, have told you about. He will lead us to victory!', 'OVERLORD_SAY_3');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910209', "What is the meaning of this, Mor'ghor?", 'LORD_ILLIDAN_SAY_1');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910210', 'But... My lord, I do not understand. $n... is the orc that has...', 'OVERLORD_SAY_4');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910211', "SILENCE!", 'LORD_ILLIDAN_SAY_2');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910212', "Blathering idiot. You incomprehensibly incompetent buffoon...", 'LORD_ILLIDAN_SAY_3');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910213', "THIS is your hero?", 'LORD_ILLIDAN_SAY_4');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910214', "You have been deceived, imbecile.", 'LORD_ILLIDAN_SAY_5');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910215', "This... whole... operation... HAS BEEN COMPROMISED!", 'LORD_ILLIDAN_SAY_6');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910216', " I expect to see this insect's carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death.!", 'LORD_ILLIDAN_SAY_7');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910217', ' It will be done, my lord.', 'OVERLORD_SAY_5');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910218', "So you thought to make a fool of Mor'ghor, eh? Before you are delivered to Lord Illidan, you will feel pain that you could not know to exist. I will take pleasure in exacting my own vengeance.", 'OVERLORD_SAY_6');
INSERT INTO `script_texts` (`entry`, `content_default`, `comment`) VALUES ('-1910219', "You will not harm the $n, Mor'ghor! Quickly, $n, climb on my back!", 'YARZILL_THE_MERC_SAY');